mapper.select:
SELECT EXISTS (
    SELECT 1 FROM TB_USER_GROUP
    WHERE USER_ID = #{userId}
      AND GROUP_ID = #{groupId}
)

SELECT EXISTS (
    SELECT 1 FROM TB_USER_GROUP WHERE GROUP_ID = #{groupId}
)

mapper.insert:
INSERT INTO TB_USER_GROUP (
    USER_ID,
    GROUP_ID,
    CREATED_BY
) VALUES (
    #{userId},
    #{groupId},
    #{createdBy}
)

mapper.delete:
DELETE FROM TB_USER_GROUP
WHERE GROUP_ID = #{groupId}
  AND USER_ID = #{userId}
